import {assert} from '@webex/test-helper-chai';
import MockWebex from '@webex/test-helper-mock-webex';
import Services from '@webex/internal-plugin-services';

/* eslint-disable no-underscore-dangle */
describe('plugin-services', () => {
  describe('ServiceCatalog', () => {
    let webex;
    let services;
    let catalog;

    beforeEach('initialize webex', () => {
      webex = new MockWebex({
        children: {
          services: Services
        }
      });

      services = webex.internal.services;
      catalog = services._getCatalog();
    });

    describe('#namespace', () => {
      it('is accurate to plugin name', () => {
        assert.equal(catalog.namespace, 'ServiceCatalog');
      });
    });

    describe('#serviceGroups', () => {
      it('has all the required keys', () => {
        assert.hasAllKeys(catalog.serviceGroups,
          ['discovery', 'preauth', 'postauth']);
      });

      it('contains at least one discovery item', () => {
        assert.isAbove(catalog.serviceGroups.discovery.length, 0);
      });

      it('contains values that are arrays', () => {
        Object.keys(catalog.serviceGroups).forEach((key) => {
          assert.typeOf(catalog.serviceGroups[key], 'array');
        });
      });
    });

    describe('#status()', () => {
      it('has all the required keys', () => {
        assert.hasAllKeys(catalog.status(),
          ['preauthorized', 'postauthorized']);
      });

      it('has valid key value types', () => {
        assert.typeOf(catalog.status().preauthorized, 'boolean');
        assert.typeOf(catalog.status().postauthorized, 'boolean');
      });

      it('has valid key values', () => {
        assert.isNotTrue(catalog.status().preauthorized);
        assert.isNotTrue(catalog.status().postauthorized);
      });
    });

    describe('#list()', () => {
      let serviceList;

      beforeEach('get services list', () => {
        serviceList = catalog.list();
      });

      it('must return an object', () => {
        assert.typeOf(serviceList, 'object');
      });

      it('returned list must be of shape {Record<string, string>}', () => {
        Object.keys(serviceList).forEach((key) => {
          assert.typeOf(key, 'string');
          assert.typeOf(serviceList[key], 'string');
        });
      });
    });
  });
});
/* eslint-enable no-underscore-dangle */
